/**
 * @param {object} gradesObject - كائن الدرجات الذي يحتوي على الأشهر والمواد والدرجات الأسبوعية والنهائية.
 * @returns {string} - التصنيف (talented, average, needs improvement, أو N/A).
 */

export function determineClassification(gradesObject) {
    const SCHOOL_MONTHS = ['sep', 'oct', 'Nov', 'Dec', 'Jan', 'Feb', 'Mar', 'Apr', 'May', 'Jun'];
    let totalScore = 0;
    let totalSubjectsCount = 0;

    // المرور على كل شهر مدرسي
    SCHOOL_MONTHS.forEach(month => {
        const subjectsInMonth = gradesObject[month];

        if (subjectsInMonth) {
            // المرور على كل مادة في هذا الشهر
            for (const subject in subjectsInMonth) {
                // التأكد من أن الدرجة النهائية (final) موجودة ورقْمية
                if (subjectsInMonth[subject] && typeof subjectsInMonth[subject].final === 'number') {
                    totalScore += subjectsInMonth[subject].final;
                    totalSubjectsCount++;
                }
            }
        }
    });

    if (totalSubjectsCount === 0) {
        return "developing";
    }

    // counting average
    const overallAverage = totalScore / totalSubjectsCount;

    // determine classification
    if (overallAverage >= 90) {
        return "superior"; // موهوب
    } else if (overallAverage >= 85) {
        return "talented"; // متوسط
    } else if (overallAverage >= 65) {
        return "developing"; // متوسط
    } else {
        return "weak"; // يحتاج تحسين
    }
}


